import React from 'react'
import { Card, CardHeader, CardBody, Box, Text } from 'grommet';
import { Phone, User, Mail } from 'grommet-icons';

export default function ContactCard(props) {
    return (
        <Card width={'medium'} height={'small'}>
            <CardHeader flex align='center' justify='center'>
                <Box direction='row'>
                    <User color='brand' size='36px' />
                    <Text color='brand' size='36px'><b>{props.name}</b></Text>
                </Box>
            </CardHeader>
            <CardBody direction='column' justify='center' align='start'>
                <Box direction='row' pad={'10px'}><Phone size='30px' color='dark-2' /><Text color='dark-2' size='24px'>{props.phone}</Text></Box>
                <Box direction='row' pad={'10px'}><Mail size='30px' color='dark-2' /><Text color='dark-2' size='24px'>{props.email}</Text></Box>
            </CardBody>
        </Card>
    )
}
