import React from 'react'
import { Card, CardHeader, CardBody, Box, Text } from 'grommet';
import { Phone, User, Mail } from 'grommet-icons';

export default function ContactCard(props) {
    return (
        <Card width={'medium'} height={'xsmall'} justify='center'>
            <CardHeader flex align='center' justify='center'>
                <Box direction='row'>
                    <User color='brand' />
                    <Text color='brand' size='24px'><b>{props.name}</b></Text>
                </Box>
            </CardHeader>
            <CardBody direction='row' justify='evenly'>
                <Phone color='accent-2' /><Text color='accent-2' size='20px'>{props.phone}</Text>
                <Mail color='accent-2' /><Text color='accent-2' size='20px'>{props.email}</Text>
            </CardBody>
        </Card>
    )
}
