import React from 'react'
import { Card, CardHeader, CardBody, Box } from 'grommet'

export default function ContactCard(props) {
    return (
        // <div className='contact-card'>
        //     <div className='contact-name'>{props.name}</div>
        //     <div className='contact-info'>
        //         <div className='contact-phone'>{props.phone}</div>
        //         <div className='contact-email'>{props.email}</div>
        //     </div>
        // </div >
        <Card height={'small'} width={'medium'} background={'light-1'} align='center'>
            <CardHeader pad="small">{props.name}</CardHeader>
            <CardBody pad="small">
                <Box direction='column' justify='around'>
                    <Box>{props.phone}</Box>
                    <Box>{props.email}</Box>
                </Box>
            </CardBody>
        </Card >
    )
}
